const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.blue,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles({
  table: {
    minWidth: 500,
  },
});

const App = () => {
  const classes = useStyles();
  const [product, setProduct] = useState([]);
  const [search, setSearch] = useState("");

  const getProductData = async () => {
    try {
      const data = await axios.get(
        "http://localhost:8080/restapi/trainings"
      );
      console.log(data.data);
      setProduct(data.data.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getProductData();
  }, []);


  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div className="App">
		<div className="App-header">
      <h1>Lets code tamil</h1>
	  <img src="https://www.freeiconspng.com/thumbs/exercise-icon/exercise-icon-1.png"className="App-logo" alt="logo" />
	  </div>
	  <br></br>

	<br></br>
	<TextField id="name" label="Name" variant="outlined" />
	<TextField id="distance" label="Distance" variant="outlined" />
	<TextField id="duration" label="Duration" variant="outlined" />
	<Button color="primary" variant="contained"> Submit </Button>
	<br></br>



      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <TableRow>
              <StyledTableCell>Name</StyledTableCell>
              <StyledTableCell >Distance</StyledTableCell>
			  <StyledTableCell >Duration</StyledTableCell>
			  <StyledTableCell ></StyledTableCell>
			  <StyledTableCell ></StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {product
              .map((item) => {
                return (
                  <StyledTableRow key={item.id}>
                    <StyledTableCell component="th" scope="row">
                      {item.name}
                    </StyledTableCell>
                    <StyledTableCell>
                      {item.distance}
                    </StyledTableCell>
					<StyledTableCell>
                      {item.duration}
                    </StyledTableCell>
					<StyledTableCell>
						<Button color="primary" variant="contained" startIcon={<EditIcon />} onClick={handleClickOpen}>
						Edit</Button>
						<Dialog
						fullScreen
						open={open}
						onClose={handleClose}
      					>
							  <TextField id="name" label="Name" variant="outlined" />
								<TextField id="distance" label="Distance" variant="outlined" />
								<TextField id="duration" label="Duration" variant="outlined" />
								<Button color="primary" variant="contained" onClick= {handleClose}> Submit </Button>
						  </Dialog>
					</StyledTableCell>
					<StyledTableCell>
						<Button color="red" variant="contained" startIcon={<DeleteIcon />}>Delete</Button>
					</StyledTableCell>
                  </StyledTableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
	  <br></br>
	  <br></br>
    </div>
  );
};




