import React from "react";
import './App.css';

import axios from "axios";
import { useEffect, useState } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import {Button} from '@material-ui/core'; //importing material ui component
import Table from "@material-ui/core/Table";
//import { DataGrid } from '@mui/x-data-grid';
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import { TableSortLabel } from "@material-ui/core";
import { TableFooter } from "@material-ui/core";
import TextField from "@material-ui/core/TextField"
import Paper from "@material-ui/core/Paper";
import DeleteIcon from "@material-ui/icons/Delete"
import EditIcon from '@material-ui/icons/Edit';

import Dialog from '@material-ui/core/Dialog';
import { DialogTitle } from "@material-ui/core";
import { DialogContent } from "@material-ui/core";
import { DialogContentText } from "@material-ui/core";
import { DialogActions } from "@material-ui/core";
import Box from '@material-ui/core/Box';

import { createTheme } from '@material-ui/core/styles';
import { red } from "@material-ui/core/colors";
import { green } from "@material-ui/core/colors";

const customtheme = createTheme({
  palette: {
    secondary: {
      light: '#efefef',
      main: '#d32f2f',
      dark: '#d3d3d3',
      contrastText: '#000',
    },
  },
});

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.common.white,
	fontSize:16
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: customtheme.palette.secondary.light,
    },
  },
}))(TableRow);

const App = () => {
  const [trainingData, setTrainingData] = useState([]);

  useEffect(() => {
	axios.get("http://localhost:8080/restapi/trainings")
		.then((response) => {
			setTrainingData(response.data.data);
		})
	}, [])

	const getData = () => {
		axios.get("http://localhost:8080/restapi/trainings")
			.then((getData) => {
				setTrainingData(getData.data.data);
			})
	}


  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSave = () => {
    setOpen(false);
  };

  	const [id, setID] = useState(null);
  	const [name, setName] = useState('');
	const [distance, setDistance] = useState('');
    const [duration, setDuration] = useState('');

  const postData = () => {
	axios.post("http://localhost:8080/restapi/trainings", {
		name,
		distance,
		duration
	})
	alert(name+distance+duration+"successfully added");
}

const setData = (data) => {
	let { id, name,distance,duration } = data;
	setID(id);
	setName(name);
	setDistance(distance);
	setDuration(duration);
}

const updateData = () => {
    axios.put(`http://localhost:8080/restapi/trainings/${id}`, {
        name,
		distance,
		duration
	})
}

  return (
    <div className="App">
		<div className="App-header">
      <h1>Lets code tamil</h1>
	  <img src="https://www.freeiconspng.com/thumbs/exercise-icon/exercise-icon-1.png"className="App-logo" alt="logo" />
	  </div>
	  <br></br>

	<br></br>
	<TextField id="name" label="Name" variant="outlined" onChange={(e) => setName(e.target.value)}/>
	<TextField id="distance" label="Distance" variant="outlined" onChange={(e) => setDistance(e.target.value)}/>
	<TextField id="duration" label="Duration" variant="outlined" onChange={(e) => setDuration(e.target.value)}/>
	<Button color="primary" variant="contained" onClick={postData}> Submit </Button>
	<br></br>
	<br></br>
	<Dialog open={open} onClose={handleClose}>
		<DialogTitle>Fill in </DialogTitle>
		<DialogContent>
		<DialogContentText>
			Enter details of training:
		</DialogContentText>
		<Box
			component="form"
			sx={{
				'& .MuiTextField-root': { m: 2, width: '15ch' },
			}}
			noValidate
			autoComplete="off"
			>
			<div>
				<TextField 
				id="name" label="Name" variant="outlined" 
				defaultValue={name} onChange={(e) => setName(e.target.value)}
				/>
				<TextField 
				id="distance" label="Distance" variant="outlined" 
				defaultValue={distance} onChange={(e) => setDistance(e.target.value)}/>
				<TextField 
				id="duration" label="Duration" variant="outlined" 
				defaultValue={duration} onChange={(e) => setDuration(e.target.value)}/>
			</div>
			</Box>
		</DialogContent>
		<DialogActions>
		<Button onClick={handleClose}>Cancel</Button>
		<Button onClick={()=>{handleSave(),updateData()}}>Save</Button>
		</DialogActions>
	</Dialog>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <StyledTableRow>
              <StyledTableCell>Name</StyledTableCell>
              <StyledTableCell >Distance</StyledTableCell>
			  <StyledTableCell >Duration</StyledTableCell>
			  <StyledTableCell ></StyledTableCell>
			  <StyledTableCell ></StyledTableCell>
            </StyledTableRow>
          </TableHead>
          <TableBody>
            {trainingData.map((item) => {
                return (
                  <StyledTableRow>
                    <StyledTableCell>
                      {item.name}
                    </StyledTableCell>
                    <StyledTableCell>
                      {item.distance}
                    </StyledTableCell>
					<StyledTableCell>
                      {item.duration}
                    </StyledTableCell>
					<StyledTableCell align='right' width='10'>
						<Button color="primary" variant="contained" onClick={() => {handleClickOpen(),setData(item)}}>Edit</Button>
					</StyledTableCell>
					<StyledTableCell align='right' width='10'>
						<Button color="secondary" variant="contained" startIcon={<DeleteIcon />}>Delete</Button>
					</StyledTableCell>
                  </StyledTableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
	  <br></br>
	  <br></br>
    </div>
  );
};




