import React from "react";
import './App.css';

import axios from "axios";
import { useEffect, useState } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import {Button} from '@material-ui/core'; //importing material ui component
import Table from "@material-ui/core/Table";
//import { DataGrid } from '@mui/x-data-grid';
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import { TableSortLabel } from "@material-ui/core";
import { TableFooter } from "@material-ui/core";
import TextField from "@material-ui/core/TextField"
import Paper from "@material-ui/core/Paper";
import DeleteIcon from "@material-ui/icons/Delete"
import EditIcon from '@material-ui/icons/Edit';

import Dialog from '@material-ui/core/Dialog';
import { DialogTitle } from "@material-ui/core";
import { DialogContent } from "@material-ui/core";
import { DialogContentText } from "@material-ui/core";
import { DialogActions } from "@material-ui/core";
import Box from '@material-ui/core/Box';

import { createTheme } from '@material-ui/core/styles';

const theme = createTheme({
  palette: {
    secondary: {
      light: '#d3d3d3',
    },
  },
});

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.primary.dark,
    color: theme.palette.common.white,
	fontSize:16
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles({
  table: {
    minWidth: 500,
  },
});

const App = () => {
  const classes = useStyles();
  const [product, setProduct] = useState([]);

  const getProductData = async () => {
    try {
      const data = await axios.get(
        "http://localhost:8080/restapi/trainings"
      );
      console.log(data.data);
      setProduct(data.data.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getProductData();
  }, []);


  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSave = (name,distance,duration) => {
    setOpen(false);
	//alert(name+" "+distance+" "+duration);
  };

  return (
    <div className="App">
		<div className="App-header">
      <h1>Lets code tamil</h1>
	  <img src="https://www.freeiconspng.com/thumbs/exercise-icon/exercise-icon-1.png"className="App-logo" alt="logo" />
	  </div>
	  <br></br>

	<br></br>
	<TextField id="name" label="Name" variant="outlined" />
	<TextField id="distance" label="Distance" variant="outlined" />
	<TextField id="duration" label="Duration" variant="outlined" />
	<Button color="primary" variant="contained"> Submit </Button>
	<br></br>
	<br></br>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 650 }} aria-label="simple table">
          <TableHead>
            <StyledTableRow>
              <StyledTableCell>Name</StyledTableCell>
              <StyledTableCell >Distance</StyledTableCell>
			  <StyledTableCell >Duration</StyledTableCell>
			  <StyledTableCell ></StyledTableCell>
			  <StyledTableCell ></StyledTableCell>
            </StyledTableRow>
          </TableHead>
          <TableBody>
            {product
              .map((item) => {
                return (
                  <StyledTableRow key={item.id}>
                    <StyledTableCell component="th" scope="row">
                      {item.name}
                    </StyledTableCell>
                    <StyledTableCell>
                      {item.distance}
                    </StyledTableCell>
					<StyledTableCell>
                      {item.duration}
                    </StyledTableCell>
					<StyledTableCell>
						<Button color="primary" variant="contained" startIcon={<EditIcon />} onClick={handleClickOpen}>
						Edit</Button>

						<Dialog open={open} onClose={handleClose}>
							<DialogTitle>Fill in </DialogTitle>
							<DialogContent>
							<DialogContentText>
								Enter details of training:
							</DialogContentText>
							<Box
								component="form"
								sx={{
									'& .MuiTextField-root': { m: 2, width: '15ch' },
								}}
								noValidate
								autoComplete="off"
								>
								<div>
									<TextField 
									required
									id="name" label="Name" variant="outlined" 
									defaultValue={item.name}/>
									<TextField 
									required 
									id="distance" label="Distance" variant="outlined" 
									defaultValue={item.distance}/>
									<TextField 
									required
									id="duration" label="Duration" variant="outlined" 
									defaultValue={item.duration}/>
								</div>
								</Box>
							</DialogContent>
							<DialogActions>
							<Button onClick={handleClose}>Cancel</Button>
							<Button onClick={handleSave}>Save</Button>
							</DialogActions>
						</Dialog>
					</StyledTableCell>
					<StyledTableCell>
						<Button color="error" variant="contained" startIcon={<DeleteIcon />}>Delete</Button>
					</StyledTableCell>
                  </StyledTableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
	  <br></br>
	  <br></br>
    </div>
  );
};




